graphql.query:
query ($query:QueryStatement $constraint:QueryStatement)
{
	hotelPaginateGuests (query:$query constraint:$constraint)
	{
		total
		count
		rows
	}
}

query ($query:QueryStatement)
{
	hotelGetGuests (query: $query)
	{
		id
		name
		lastname
		code
		createdAt
		updatedAt
	}
}

query ($query:QueryStatement)
{
	hotelFindGuest (query: $query)
	{
		id
		name
		lastname
		code
		createdAt
		updatedAt
	}
}

query ($id:ID)
{
	hotelFindGuestById (id:$id)
	{
		id
		name
		lastname
		code
		createdAt
		updatedAt
	}
}

mutation ($payload:HotelUpdateGuestInput!)
{
	hotelUpdateGuest (payload:$payload)
	{
		id
		name
		lastname
		code
		createdAt
		updatedAt
	}
}

mutation ($id:ID!)
{
	hotelDeleteGuestById (id:$id)
	{
		id
		name
		lastname
		code
		createdAt
		updatedAt
	}
}

mutation ($query:QueryStatement)
{
	hotelDeleteGuests (query: $query)
	{
		id
		name
		lastname
		code
		createdAt
		updatedAt
	}
}

